    global.set("global_alarma_ea" + i, "null", "file")



msg.payload = [];
return msg;

let tabla = global.get("tabla_listadoAlarmasActivas", "file")// || []; 
let soloReemplazarDatos = true; //cuando no es necesario recontruir y evitamos que la tablase vaya a la posición inicial visualmente

let elementosConCambio = [];

for (let i of global.get("numAlarmasPLC_general")){
    var numeroAlarma = i;
    var nombreAlarma = global.get("nombreAlarma" + numeroAlarma);
    var estadoAnteriorAlarma = global.get("global_alarma_ea" + numeroAlarma, "file") || "no";
    var estadoAlarma = global.get("global_alarma" + numeroAlarma);
    var estado_inhibicionAlarma = global.get("alarmaInhibida"  + numeroAlarma);
    let soloReemplazarDatos_individual = false;

    if (estadoAlarma != estadoAnteriorAlarma){

        elementosConCambio.unshift({
            numeroAlarma: i,
            estadoAlarma: estadoAlarma
            });

        // Buscar si ya existe una alarma con ese ID
        let index = tabla.findIndex(item => item.id === numeroAlarma);
        let ahora = new Date();
        //let fechaLegible = ahora.toLocaleString("es-CL");  // formato local: dd/mm/yyyy hh:mm:ss

        let fechaLegible = ahora.toLocaleString("es-CL", {
    timeZone: "UTC",
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit"
});

        if (estadoAlarma == "activa_noReconocida" || estadoAlarma == "inactiva_noReconocida"){ 
            let color = global.get("global_color_alarma"); 
            if (estadoAlarma == "inactiva_noReconocida")
                color = global.get("global_color_inactivaNoReconocida");
            let blink = true;
            let alarmaHtml = `<div class="alarm-cell ${color} ${blink ? 'blinking' : ''}">${nombreAlarma}</div>`;

            let elemento = {
                "id": numeroAlarma,
                "alarma": alarmaHtml,            
                "fecha": fechaLegible
            }
            if (index !== -1) {               
                // Ya existe → reemplazar
                tabla[index] = elemento;
                soloReemplazarDatos_individual = true;    
            }
            else 
                tabla.unshift(elemento);            
        }
        else if (estadoAlarma == "activa_reconocida"){
            let color = global.get("global_color_alarma");
            let blink = false;        
            let alarmaHtml = `<div class="alarm-cell ${color} ${blink ? 'blinking' : ''}">${nombreAlarma}</div>`;
            let elemento = {
                "id": numeroAlarma,
                "alarma": alarmaHtml,            
                "fecha": fechaLegible
            }
            if (index !== -1){
                // Ya existe → reemplazar
                tabla[index] = elemento; 
                soloReemplazarDatos_individual = true;
                }
            else
                tabla.unshift(elemento);              
        }
        else if (estadoAlarma == "inactiva_reconocida"){ //se saca de la tabla
            if (index !== -1) 
                // Ya existe → eliminar            
                tabla.splice(index, 1);   
                soloReemplazarDatos_individual = true;                     
        }
        if(!soloReemplazarDatos_individual){
            soloReemplazarDatos = false;
        }
    }   
}    




global.set("tabla_listadoAlarmasActivas", tabla, "file"); 

    if(soloReemplazarDatos){
        msg.payload = {
            command: "replaceData",
            arguments: [tabla]  // tu array con datos actualizados
        };
    }
    else
