            } else if (!reconocida[i] && alarma[i]) {
                global.set("global_alarma" + i, "activa_noReconocida");
            }else {
                global.set("global_alarma" + i, "inactiva_reconocida");
            }
            global.set("global_actualizarBotones", "actualizar");
        }
    }
}

